interface Content {
	title: string;
	href?: string;
	Icon: ((props?: SVGProps<any>) => ReactNode) | LucideIcon;
	isNew?: boolean;
	list: {
		title: string;
		href: string;
		icon: ((props?: SVGProps<any>) => ReactNode) | LucideIcon;
		group?: boolean;
		isNew?: boolean;
	}[];
}

export function getPageTree(): PageTree.Root {
	return {
		$id: "root",
		name: "docs",
		children: [
			{
				type: "folder",
				root: true,
				name: "Docs",
				description: "get started, concepts, and plugins.",
				children: contents.map(contentToPageTree),
			},
		],
	};
}

function contentToPageTree(content: Content): PageTree.Folder {
	return {
		type: "folder",
		icon: <content.Icon />,
		name: content.title,
		index: content.href
			? {
					icon: <content.Icon />,
					name: content.title,
					type: "page",
					url: content.href,
				}
			: undefined,
		children: content.list.map((item) => ({
			type: "page",
			url: item.href,
			name: item.title,
			icon: <item.icon />,
		})),
	};
}

